import React, { useState, useEffect } from "react";
import axios from "axios";
import {
    MDBTable,
    MDBTableHead,
    MDBTableBody,
    MDBRow,
    MDBCol,
    MDBContainer,
    MDBBtn,
    MDBBtnGroup,
    MDBPagination,
    MDBPaginationItem,
    MDBPaginationLink,
} from "mdb-react-ui-kit";



function RelTable() {
    const [data, setData] = useState([]);
    const [value, setValue] = useState("");
    const [sortValue, setSortValue] = useState("");
    const [currentPage, setCurrentPage] = useState(0);
    const [pageLimit] = useState(4);
    const [sortFilterValue, setSortFilterValue] = useState("");
    const [operation, setOperation] = useState("");
    const sortOptions = ["Data de InÃ­cio", "Data Fim", "Total de Erros"];

    useEffect(() => {
        loadUsersData(0, 4, 0);
    }, []);

    const loadUsersData = async (
        start,
        end,
        increase,
        optType = null,
        filterOrSortValue
    ) => {
        switch (optType) {
            case "search":
                setOperation(optType);
                setSortValue("");
                return await axios
                    .get(
                        `http://localhost:3001/users?q=${value}&_start=${start}&_end=${end}`
                    )
                    .then((response) => {
                        setData(response.data);
                        setCurrentPage(currentPage + increase);
                    })
                    .catch((err) => console.log(err));
            case "sort":
                setOperation(optType);
                setSortFilterValue(filterOrSortValue);
                return await axios
                    .get(
                        `http://localhost:5000/users?_sort=${filterOrSortValue}&_order=asc&_start=${start}&_end=${end}`
                    )
                    .then((response) => {
                        setData(response.data);
                        setCurrentPage(currentPage + increase);
                    })
                    .catch((err) => console.log(err));
            case "filter":
                setOperation(optType);
                setSortFilterValue(filterOrSortValue);
                return await axios
                    .get(
                        `http://localhost:5000/users?status=${filterOrSortValue}&_order=asc&_start=${start}&_end=${end}`
                    )
                    .then((response) => {
                        setData(response.data);
                        setCurrentPage(currentPage + increase);
                    })
                    .catch((err) => console.log(err));
            default:
                return await axios
                    .get(`http://localhost:3001/relatorios`)
                    .then((response) => {
                        setData(response.data);
                        setCurrentPage(currentPage + increase);
                    })
                    .catch((err) => console.log(err));
        }
    };

    console.log("data", data);


    const handleReset = () => {
        setOperation("");
        setValue("");
        setSortFilterValue("");
        setSortValue("");
        loadUsersData(0, 4, 0);
    };
    const handleSearch = async (e) => {
        e.preventDefault();
        loadUsersData(0, 4, 0, "search");
        // return await axios
        //   .get(`http://localhost:5000/users?q=${value}`)
        //   .then((response) => {
        //     setData(response.data);
        //     setValue("");
        //   })
        //   .catch((err) => console.log(err));
    };

    const handleSort = async (e) => {
        let value = e.target.value;
        loadUsersData(0, 4, 0, "sort", value);
        setSortValue(value);

        // return await axios
        //   .get(`http://localhost:5000/users?_sort=${value}&_order=asc`)
        //   .then((response) => {
        //     setData(response.data);
        //   })
        //   .catch((err) => console.log(err));
    };

    const handleFilter = async (value) => {
        loadUsersData(0, 4, 0, "filter", value);
        // return await axios
        //   .get(`http://localhost:5000/users?status=${value}`)
        //   .then((response) => {
        //     setData(response.data);
        //   })
        //   .catch((err) => console.log(err));
    };


    const renderPagination = () => {
        if (data.length < 4 && currentPage === 0) return null;
        if (currentPage === 0) {
            return (
                <MDBPagination className="mb-0">
                    <MDBPaginationItem>
                        <MDBPaginationLink>1</MDBPaginationLink>
                    </MDBPaginationItem>
                    <MDBPaginationItem>
                        <MDBBtn onClick={() => loadUsersData(4, 8, 1, operation)}>
                            Next
                        </MDBBtn>
                    </MDBPaginationItem>
                </MDBPagination>
            );
        } else if (currentPage < pageLimit - 1 && data.length === pageLimit) {
            return (
                <MDBPagination className="mb-0">
                    <MDBPaginationItem>
                        <MDBBtn
                            onClick={() =>
                                loadUsersData(
                                    (currentPage - 1) * 4,
                                    currentPage * 4,
                                    -1,
                                    operation,
                                    sortFilterValue
                                )
                            }
                        >
                            Prev
                        </MDBBtn>
                    </MDBPaginationItem>
                    <MDBPaginationItem>
                        <MDBPaginationLink>{currentPage + 1}</MDBPaginationLink>
                    </MDBPaginationItem>

                    <MDBPaginationItem>
                        <MDBBtn
                            onClick={() =>
                                loadUsersData(
                                    (currentPage + 1) * 4,
                                    (currentPage + 2) * 4,
                                    1,
                                    operation,
                                    sortFilterValue
                                )
                            }
                        >
                            Next
                        </MDBBtn>
                    </MDBPaginationItem>
                </MDBPagination>
            );
        } else {
            return (
                <MDBPagination className="mb-0">
                    <MDBPaginationItem>
                        <MDBBtn
                            onClick={() =>
                                loadUsersData(
                                    (currentPage - 1) * 4,
                                    currentPage * 4,
                                    -1,
                                    operation
                                )
                            }
                        >
                            Prev
                        </MDBBtn>
                    </MDBPaginationItem>
                    <MDBPaginationItem>
                        <MDBPaginationLink>{currentPage + 1}</MDBPaginationLink>
                    </MDBPaginationItem>
                </MDBPagination>
            );
        }
    };

    return (
        <MDBContainer>
            <form
                style={{
                    width: "50%", borderRadius: "2px", height: "35px", marginBottom: "20px",
                    margin: "auto",
                    padding: "30px",
                    maxWidth: "500px",
                    alignContent: "start",
                }}
                className="d-flex input-group w-auto"
                onSubmit={handleSearch}
            >
                <input
                    type="text"
                    className="form-control"
                    placeholder="Pesquisa "
                    value={value}
                    onChange={(e) => setValue(e.target.value)}
                />
                <div class='parent'>
                    <button style={{ borderRadius: "12px", backgroundColor: "gray" }} className="child" type="submit" color="light">
                        Search
                    </button>
